import React from "react";
import '../styles/Abm.css';
import Navbar from "./Navbar";
import { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getInfo, registerOperation } from '../actions';
import Modal from 'react-modal';



Modal.setAppElement('#root');


export default function dbamanager() {
    const [input, setInput]= useState({
        user: "",
        pass: ""
    })
    const handleInput = (e)=>{
        setInput({
            ...input,
            [e.target.name]: e.target.value
        })
    }
    const handleSubmit = async (e)=>{

        let json = axios.post("http://localhost:3001/login", input);
    }
    return (
        <div className="div-main-abm">
            <Navbar />
            <h2>ABM DE SERIES</h2>
            <div className="div-balance-title div-abm-title">Modulo de ingreso</div>
            <form  className="div-main-form">
                <label>Usuario</label>
                <input name="user" value={input.user} onChange={(e)=>handleInput(e)} placeholder="Ingrese el usuario" type='text' ></input>
                <label>Contraseña</label>
                <input name="pass" value={input.pass} onChange={(e)=>handleInput(e)} placeholder="Ingrese la contraseña" type='text' ></input>
                

                <button type="submit" className="button-form">Ingresar</button>
            </form>

        </div>
    )
}
